INSERT INTO frameworks (id, name, version, description, category, is_system, created_at)
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0000-000000000001', 'b0000000-0000-0000-0000-000000000001', '4', 'Context of the Organization', 'Requirements for understanding the organization and its context.', 'Management System', NULL, 1);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0001-000000000001', 'b0000000-0000-0000-0000-000000000001', '4.1', 'Understanding the Organization and its Context', 'The organization shall determine external and internal issues that are relevant to its purpose and that affect its ability to achieve the intended outcome(s) of its information security management system.', 'Management System', 'b1000000-0000-0000-0000-000000000001', 1),
('b1000000-0000-0000-0001-000000000002', 'b0000000-0000-0000-0000-000000000001', '4.2', 'Understanding the Needs and Expectations of Interested Parties', 'The organization shall determine the interested parties that are relevant to the ISMS, the requirements of these interested parties relevant to information security, and which of these requirements will be addressed through the ISMS.', 'Management System', 'b1000000-0000-0000-0000-000000000001', 2),
('b1000000-0000-0000-0001-000000000003', 'b0000000-0000-0000-0000-000000000001', '4.3', 'Determining the Scope of the ISMS', 'The organization shall determine the boundaries and applicability of the ISMS to establish its scope.', 'Management System', 'b1000000-0000-0000-0000-000000000001', 3),
('b1000000-0000-0000-0001-000000000004', 'b0000000-0000-0000-0000-000000000001', '4.4', 'Information Security Management System', 'The organization shall establish, implement, maintain, and continually improve an ISMS in accordance with the requirements of this document.', 'Management System', 'b1000000-0000-0000-0000-000000000001', 4);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0000-000000000002', 'b0000000-0000-0000-0000-000000000001', '5', 'Leadership', 'Requirements for leadership and commitment to the ISMS.', 'Management System', NULL, 2);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0002-000000000001', 'b0000000-0000-0000-0000-000000000001', '5.1', 'Leadership and Commitment', 'Top management shall demonstrate leadership and commitment with respect to the ISMS.', 'Management System', 'b1000000-0000-0000-0000-000000000002', 1),
('b1000000-0000-0000-0002-000000000002', 'b0000000-0000-0000-0000-000000000001', '5.2', 'Policy', 'Top management shall establish an information security policy that is appropriate, includes objectives, includes commitment to satisfy requirements, and includes commitment to continual improvement.', 'Management System', 'b1000000-0000-0000-0000-000000000002', 2),
('b1000000-0000-0000-0002-000000000003', 'b0000000-0000-0000-0000-000000000001', '5.3', 'Organizational Roles, Responsibilities and Authorities', 'Top management shall ensure that responsibilities and authorities for roles relevant to information security are assigned and communicated.', 'Management System', 'b1000000-0000-0000-0000-000000000002', 3);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0000-000000000003', 'b0000000-0000-0000-0000-000000000001', '6', 'Planning', 'Requirements for planning to address risks and opportunities.', 'Management System', NULL, 3);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0003-000000000001', 'b0000000-0000-0000-0000-000000000001', '6.1', 'Actions to Address Risks and Opportunities', 'When planning for the ISMS, the organization shall consider issues and requirements and determine risks and opportunities that need to be addressed.', 'Management System', 'b1000000-0000-0000-0000-000000000003', 1),
('b1000000-0000-0000-0003-000000000002', 'b0000000-0000-0000-0000-000000000001', '6.2', 'Information Security Objectives and Planning', 'The organization shall establish information security objectives at relevant functions and levels.', 'Management System', 'b1000000-0000-0000-0000-000000000003', 2),
('b1000000-0000-0000-0003-000000000003', 'b0000000-0000-0000-0000-000000000001', '6.3', 'Planning of Changes', 'When the organization determines the need for changes to the ISMS, the changes shall be carried out in a planned manner.', 'Management System', 'b1000000-0000-0000-0000-000000000003', 3);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0000-000000000004', 'b0000000-0000-0000-0000-000000000001', '7', 'Support', 'Requirements for resources, competence, awareness, communication, and documented information.', 'Management System', NULL, 4);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0004-000000000001', 'b0000000-0000-0000-0000-000000000001', '7.1', 'Resources', 'The organization shall determine and provide the resources needed for the establishment, implementation, maintenance, and continual improvement of the ISMS.', 'Management System', 'b1000000-0000-0000-0000-000000000004', 1),
('b1000000-0000-0000-0004-000000000002', 'b0000000-0000-0000-0000-000000000001', '7.2', 'Competence', 'The organization shall determine the necessary competence of persons doing work that affects information security performance.', 'Management System', 'b1000000-0000-0000-0000-000000000004', 2),
('b1000000-0000-0000-0004-000000000003', 'b0000000-0000-0000-0000-000000000001', '7.3', 'Awareness', 'Persons doing work under the organization''s control shall be aware of the information security policy, their contribution to the ISMS effectiveness, and implications of not conforming.', 'Management System', 'b1000000-0000-0000-0000-000000000004', 3),
('b1000000-0000-0000-0004-000000000004', 'b0000000-0000-0000-0000-000000000001', '7.4', 'Communication', 'The organization shall determine the need for internal and external communications relevant to the ISMS.', 'Management System', 'b1000000-0000-0000-0000-000000000004', 4),
('b1000000-0000-0000-0004-000000000005', 'b0000000-0000-0000-0000-000000000001', '7.5', 'Documented Information', 'The organization''s ISMS shall include documented information required by this document and determined by the organization as necessary for ISMS effectiveness.', 'Management System', 'b1000000-0000-0000-0000-000000000004', 5);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0000-000000000005', 'b0000000-0000-0000-0000-000000000001', '8', 'Operation', 'Requirements for operational planning and control.', 'Management System', NULL, 5);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0005-000000000001', 'b0000000-0000-0000-0000-000000000001', '8.1', 'Operational Planning and Control', 'The organization shall plan, implement, and control the processes needed to meet information security requirements.', 'Management System', 'b1000000-0000-0000-0000-000000000005', 1),
('b1000000-0000-0000-0005-000000000002', 'b0000000-0000-0000-0000-000000000001', '8.2', 'Information Security Risk Assessment', 'The organization shall perform information security risk assessments at planned intervals or when significant changes are proposed or occur.', 'Management System', 'b1000000-0000-0000-0000-000000000005', 2),
('b1000000-0000-0000-0005-000000000003', 'b0000000-0000-0000-0000-000000000001', '8.3', 'Information Security Risk Treatment', 'The organization shall implement the information security risk treatment plan.', 'Management System', 'b1000000-0000-0000-0000-000000000005', 3);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0000-000000000006', 'b0000000-0000-0000-0000-000000000001', '9', 'Performance Evaluation', 'Requirements for monitoring, measurement, analysis, and evaluation.', 'Management System', NULL, 6);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0006-000000000001', 'b0000000-0000-0000-0000-000000000001', '9.1', 'Monitoring, Measurement, Analysis and Evaluation', 'The organization shall determine what needs to be monitored and measured, the methods for monitoring, measurement, analysis, and evaluation.', 'Management System', 'b1000000-0000-0000-0000-000000000006', 1),
('b1000000-0000-0000-0006-000000000002', 'b0000000-0000-0000-0000-000000000001', '9.2', 'Internal Audit', 'The organization shall conduct internal audits at planned intervals to provide information on whether the ISMS conforms to requirements.', 'Management System', 'b1000000-0000-0000-0000-000000000006', 2),
('b1000000-0000-0000-0006-000000000003', 'b0000000-0000-0000-0000-000000000001', '9.3', 'Management Review', 'Top management shall review the organization''s ISMS at planned intervals to ensure its continuing suitability, adequacy, and effectiveness.', 'Management System', 'b1000000-0000-0000-0000-000000000006', 3);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0000-000000000007', 'b0000000-0000-0000-0000-000000000001', '10', 'Improvement', 'Requirements for continual improvement of the ISMS.', 'Management System', NULL, 7);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b1000000-0000-0000-0007-000000000001', 'b0000000-0000-0000-0000-000000000001', '10.1', 'Continual Improvement', 'The organization shall continually improve the suitability, adequacy, and effectiveness of the ISMS.', 'Management System', 'b1000000-0000-0000-0000-000000000007', 1),
('b1000000-0000-0000-0007-000000000002', 'b0000000-0000-0000-0000-000000000001', '10.2', 'Nonconformity and Corrective Action', 'When a nonconformity occurs, the organization shall react, evaluate, implement actions, review effectiveness, and make changes to the ISMS if necessary.', 'Management System', 'b1000000-0000-0000-0000-000000000007', 2);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b2000000-0000-0000-0000-000000000001', 'b0000000-0000-0000-0000-000000000001', 'A.5', 'Organizational Controls', 'Controls related to organizational policies, procedures, and management.', 'Organizational Controls', NULL, 10);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b2000000-0000-0000-0001-000000000001', 'b0000000-0000-0000-0000-000000000001', 'A.5.1', 'Policies for Information Security', 'Information security policy and topic-specific policies shall be defined, approved by management, published, communicated to and acknowledged by relevant personnel and relevant interested parties, and reviewed at planned intervals and if significant changes occur.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 1),
('b2000000-0000-0000-0001-000000000002', 'b0000000-0000-0000-0000-000000000001', 'A.5.2', 'Information Security Roles and Responsibilities', 'Information security roles and responsibilities shall be defined and allocated according to the organization needs.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 2),
('b2000000-0000-0000-0001-000000000003', 'b0000000-0000-0000-0000-000000000001', 'A.5.3', 'Segregation of Duties', 'Conflicting duties and conflicting areas of responsibility shall be segregated.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 3),
('b2000000-0000-0000-0001-000000000004', 'b0000000-0000-0000-0000-000000000001', 'A.5.4', 'Management Responsibilities', 'Management shall require all personnel to apply information security in accordance with the established information security policy, topic-specific policies, and procedures of the organization.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 4),
('b2000000-0000-0000-0001-000000000005', 'b0000000-0000-0000-0000-000000000001', 'A.5.5', 'Contact with Authorities', 'The organization shall establish and maintain contact with relevant authorities.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 5),
('b2000000-0000-0000-0001-000000000006', 'b0000000-0000-0000-0000-000000000001', 'A.5.6', 'Contact with Special Interest Groups', 'The organization shall establish and maintain contact with special interest groups or other specialist security forums and professional associations.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 6),
('b2000000-0000-0000-0001-000000000007', 'b0000000-0000-0000-0000-000000000001', 'A.5.7', 'Threat Intelligence', 'Information relating to information security threats shall be collected and analysed to produce threat intelligence.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 7),
('b2000000-0000-0000-0001-000000000008', 'b0000000-0000-0000-0000-000000000001', 'A.5.8', 'Information Security in Project Management', 'Information security shall be integrated into project management.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 8),
('b2000000-0000-0000-0001-000000000009', 'b0000000-0000-0000-0000-000000000001', 'A.5.9', 'Inventory of Information and Other Associated Assets', 'An inventory of information and other associated assets, including owners, shall be developed and maintained.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 9),
('b2000000-0000-0000-0001-000000000010', 'b0000000-0000-0000-0000-000000000001', 'A.5.10', 'Acceptable Use of Information and Other Associated Assets', 'Rules for the acceptable use and procedures for handling information and other associated assets shall be identified, documented, and implemented.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 10),
('b2000000-0000-0000-0001-000000000011', 'b0000000-0000-0000-0000-000000000001', 'A.5.11', 'Return of Assets', 'Personnel and other interested parties as appropriate shall return all the organization''s assets in their possession upon change or termination of their employment, contract, or agreement.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 11),
('b2000000-0000-0000-0001-000000000012', 'b0000000-0000-0000-0000-000000000001', 'A.5.12', 'Classification of Information', 'Information shall be classified according to the information security needs of the organization based on confidentiality, integrity, availability, and relevant interested party requirements.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 12),
('b2000000-0000-0000-0001-000000000013', 'b0000000-0000-0000-0000-000000000001', 'A.5.13', 'Labelling of Information', 'An appropriate set of procedures for information labelling shall be developed and implemented in accordance with the information classification scheme adopted by the organization.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 13),
('b2000000-0000-0000-0001-000000000014', 'b0000000-0000-0000-0000-000000000001', 'A.5.14', 'Information Transfer', 'Information transfer rules, procedures, or agreements shall be in place for all types of transfer facilities within the organization and between the organization and other parties.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 14),
('b2000000-0000-0000-0001-000000000015', 'b0000000-0000-0000-0000-000000000001', 'A.5.15', 'Access Control', 'Rules to control physical and logical access to information and other associated assets shall be established and implemented based on business and information security requirements.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 15),
('b2000000-0000-0000-0001-000000000016', 'b0000000-0000-0000-0000-000000000001', 'A.5.16', 'Identity Management', 'The full life cycle of identities shall be managed.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 16),
('b2000000-0000-0000-0001-000000000017', 'b0000000-0000-0000-0000-000000000001', 'A.5.17', 'Authentication Information', 'Allocation and management of authentication information shall be controlled by a management process, including advising personnel on appropriate handling of authentication information.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 17),
('b2000000-0000-0000-0001-000000000018', 'b0000000-0000-0000-0000-000000000001', 'A.5.18', 'Access Rights', 'Access rights to information and other associated assets shall be provisioned, reviewed, modified, and removed in accordance with the organization''s topic-specific policy on and rules for access control.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 18),
('b2000000-0000-0000-0001-000000000019', 'b0000000-0000-0000-0000-000000000001', 'A.5.19', 'Information Security in Supplier Relationships', 'Processes and procedures shall be defined and implemented to manage the information security risks associated with the use of supplier''s products or services.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 19),
('b2000000-0000-0000-0001-000000000020', 'b0000000-0000-0000-0000-000000000001', 'A.5.20', 'Addressing Information Security Within Supplier Agreements', 'Relevant information security requirements shall be established and agreed with each supplier based on the type of supplier relationship.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 20),
('b2000000-0000-0000-0001-000000000021', 'b0000000-0000-0000-0000-000000000001', 'A.5.21', 'Managing Information Security in the ICT Supply Chain', 'Processes and procedures shall be defined and implemented to manage the information security risks associated with the ICT products and services supply chain.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 21),
('b2000000-0000-0000-0001-000000000022', 'b0000000-0000-0000-0000-000000000001', 'A.5.22', 'Monitoring, Review and Change Management of Supplier Services', 'The organization shall regularly monitor, review, evaluate, and manage change in supplier information security practices and service delivery.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 22),
('b2000000-0000-0000-0001-000000000023', 'b0000000-0000-0000-0000-000000000001', 'A.5.23', 'Information Security for Use of Cloud Services', 'Processes for acquisition, use, management, and exit from cloud services shall be established in accordance with the organization''s information security requirements.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 23),
('b2000000-0000-0000-0001-000000000024', 'b0000000-0000-0000-0000-000000000001', 'A.5.24', 'Information Security Incident Management Planning and Preparation', 'The organization shall plan and prepare for managing information security incidents by defining, establishing, and communicating information security incident management processes, roles, and responsibilities.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 24),
('b2000000-0000-0000-0001-000000000025', 'b0000000-0000-0000-0000-000000000001', 'A.5.25', 'Assessment and Decision on Information Security Events', 'The organization shall assess information security events and decide if they are to be categorized as information security incidents.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 25),
('b2000000-0000-0000-0001-000000000026', 'b0000000-0000-0000-0000-000000000001', 'A.5.26', 'Response to Information Security Incidents', 'Information security incidents shall be responded to in accordance with the documented procedures.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 26),
('b2000000-0000-0000-0001-000000000027', 'b0000000-0000-0000-0000-000000000001', 'A.5.27', 'Learning from Information Security Incidents', 'Knowledge gained from information security incidents shall be used to strengthen and improve the information security controls.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 27),
('b2000000-0000-0000-0001-000000000028', 'b0000000-0000-0000-0000-000000000001', 'A.5.28', 'Collection of Evidence', 'The organization shall establish and implement procedures for the identification, collection, acquisition, and preservation of evidence related to information security events.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 28),
('b2000000-0000-0000-0001-000000000029', 'b0000000-0000-0000-0000-000000000001', 'A.5.29', 'Information Security During Disruption', 'The organization shall plan how to maintain information security at an appropriate level during disruption.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 29),
('b2000000-0000-0000-0001-000000000030', 'b0000000-0000-0000-0000-000000000001', 'A.5.30', 'ICT Readiness for Business Continuity', 'ICT readiness shall be planned, implemented, maintained, and tested based on business continuity objectives and ICT continuity requirements.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 30),
('b2000000-0000-0000-0001-000000000031', 'b0000000-0000-0000-0000-000000000001', 'A.5.31', 'Legal, Statutory, Regulatory and Contractual Requirements', 'Legal, statutory, regulatory, and contractual requirements relevant to information security and the organization''s approach to meet these requirements shall be identified, documented, and kept up to date.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 31),
('b2000000-0000-0000-0001-000000000032', 'b0000000-0000-0000-0000-000000000001', 'A.5.32', 'Intellectual Property Rights', 'The organization shall implement appropriate procedures to protect intellectual property rights.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 32),
('b2000000-0000-0000-0001-000000000033', 'b0000000-0000-0000-0000-000000000001', 'A.5.33', 'Protection of Records', 'Records shall be protected from loss, destruction, falsification, unauthorized access, and unauthorized release.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 33),
('b2000000-0000-0000-0001-000000000034', 'b0000000-0000-0000-0000-000000000001', 'A.5.34', 'Privacy and Protection of PII', 'The organization shall identify and meet the requirements regarding the preservation of privacy and protection of PII according to applicable laws and regulations and contractual requirements.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 34),
('b2000000-0000-0000-0001-000000000035', 'b0000000-0000-0000-0000-000000000001', 'A.5.35', 'Independent Review of Information Security', 'The organization''s approach to managing information security and its implementation including people, processes, and technologies shall be reviewed independently at planned intervals, or when significant changes occur.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 35),
('b2000000-0000-0000-0001-000000000036', 'b0000000-0000-0000-0000-000000000001', 'A.5.36', 'Compliance with Policies, Rules and Standards', 'Compliance with the organization''s information security policy, topic-specific policies, rules, and standards shall be regularly reviewed.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 36),
('b2000000-0000-0000-0001-000000000037', 'b0000000-0000-0000-0000-000000000001', 'A.5.37', 'Documented Operating Procedures', 'Operating procedures for information processing facilities shall be documented and made available to personnel who need them.', 'Organizational Controls', 'b2000000-0000-0000-0000-000000000001', 37);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b2000000-0000-0000-0000-000000000002', 'b0000000-0000-0000-0000-000000000001', 'A.6', 'People Controls', 'Controls related to personnel security before, during, and after employment.', 'People Controls', NULL, 11);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b2000000-0000-0000-0002-000000000001', 'b0000000-0000-0000-0000-000000000001', 'A.6.1', 'Screening', 'Background verification checks on all candidates for employment shall be carried out prior to joining the organization and on an ongoing basis taking into account applicable laws, regulations, and ethics.', 'People Controls', 'b2000000-0000-0000-0000-000000000002', 1),
('b2000000-0000-0000-0002-000000000002', 'b0000000-0000-0000-0000-000000000001', 'A.6.2', 'Terms and Conditions of Employment', 'The employment contractual agreements shall state the personnel''s and the organization''s responsibilities for information security.', 'People Controls', 'b2000000-0000-0000-0000-000000000002', 2),
('b2000000-0000-0000-0002-000000000003', 'b0000000-0000-0000-0000-000000000001', 'A.6.3', 'Information Security Awareness, Education and Training', 'Personnel of the organization and relevant interested parties shall receive appropriate information security awareness, education, and training and regular updates of the organization''s information security policy, topic-specific policies, and procedures.', 'People Controls', 'b2000000-0000-0000-0000-000000000002', 3),
('b2000000-0000-0000-0002-000000000004', 'b0000000-0000-0000-0000-000000000001', 'A.6.4', 'Disciplinary Process', 'A disciplinary process shall be formalized and communicated to take actions against personnel and other relevant interested parties who have committed an information security policy violation.', 'People Controls', 'b2000000-0000-0000-0000-000000000002', 4),
('b2000000-0000-0000-0002-000000000005', 'b0000000-0000-0000-0000-000000000001', 'A.6.5', 'Responsibilities After Termination or Change of Employment', 'Information security responsibilities and duties that remain valid after termination or change of employment shall be defined, enforced, and communicated to relevant personnel and other interested parties.', 'People Controls', 'b2000000-0000-0000-0000-000000000002', 5),
('b2000000-0000-0000-0002-000000000006', 'b0000000-0000-0000-0000-000000000001', 'A.6.6', 'Confidentiality or Non-Disclosure Agreements', 'Confidentiality or non-disclosure agreements reflecting the organization''s needs for the protection of information shall be identified, documented, regularly reviewed, and signed by personnel and other relevant interested parties.', 'People Controls', 'b2000000-0000-0000-0000-000000000002', 6),
('b2000000-0000-0000-0002-000000000007', 'b0000000-0000-0000-0000-000000000001', 'A.6.7', 'Remote Working', 'Security measures shall be implemented when personnel are working remotely to protect information accessed, processed, or stored outside the organization''s premises.', 'People Controls', 'b2000000-0000-0000-0000-000000000002', 7),
('b2000000-0000-0000-0002-000000000008', 'b0000000-0000-0000-0000-000000000001', 'A.6.8', 'Information Security Event Reporting', 'The organization shall provide a mechanism for personnel to report observed or suspected information security events through appropriate channels in a timely manner.', 'People Controls', 'b2000000-0000-0000-0000-000000000002', 8);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b2000000-0000-0000-0000-000000000003', 'b0000000-0000-0000-0000-000000000001', 'A.7', 'Physical Controls', 'Controls related to physical security of premises and equipment.', 'Physical Controls', NULL, 12);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b2000000-0000-0000-0003-000000000001', 'b0000000-0000-0000-0000-000000000001', 'A.7.1', 'Physical Security Perimeters', 'Security perimeters shall be defined and used to protect areas that contain information and other associated assets.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 1),
('b2000000-0000-0000-0003-000000000002', 'b0000000-0000-0000-0000-000000000001', 'A.7.2', 'Physical Entry', 'Secure areas shall be protected by appropriate entry controls and access points.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 2),
('b2000000-0000-0000-0003-000000000003', 'b0000000-0000-0000-0000-000000000001', 'A.7.3', 'Securing Offices, Rooms and Facilities', 'Physical security for offices, rooms, and facilities shall be designed and implemented.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 3),
('b2000000-0000-0000-0003-000000000004', 'b0000000-0000-0000-0000-000000000001', 'A.7.4', 'Physical Security Monitoring', 'Premises shall be continuously monitored for unauthorized physical access.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 4),
('b2000000-0000-0000-0003-000000000005', 'b0000000-0000-0000-0000-000000000001', 'A.7.5', 'Protecting Against Physical and Environmental Threats', 'Protection against physical and environmental threats, such as natural disasters and other intentional or unintentional physical threats to infrastructure shall be designed and implemented.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 5),
('b2000000-0000-0000-0003-000000000006', 'b0000000-0000-0000-0000-000000000001', 'A.7.6', 'Working in Secure Areas', 'Security measures for working in secure areas shall be designed and implemented.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 6),
('b2000000-0000-0000-0003-000000000007', 'b0000000-0000-0000-0000-000000000001', 'A.7.7', 'Clear Desk and Clear Screen', 'Clear desk rules for papers and removable storage media and clear screen rules for information processing facilities shall be defined and appropriately enforced.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 7),
('b2000000-0000-0000-0003-000000000008', 'b0000000-0000-0000-0000-000000000001', 'A.7.8', 'Equipment Siting and Protection', 'Equipment shall be sited securely and protected.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 8),
('b2000000-0000-0000-0003-000000000009', 'b0000000-0000-0000-0000-000000000001', 'A.7.9', 'Security of Assets Off-Premises', 'Off-site assets shall be protected.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 9),
('b2000000-0000-0000-0003-000000000010', 'b0000000-0000-0000-0000-000000000001', 'A.7.10', 'Storage Media', 'Storage media shall be managed through their life cycle of acquisition, use, transportation, and disposal in accordance with the organization''s classification scheme and handling requirements.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 10),
('b2000000-0000-0000-0003-000000000011', 'b0000000-0000-0000-0000-000000000001', 'A.7.11', 'Supporting Utilities', 'Information processing facilities shall be protected from power failures and other disruptions caused by failures in supporting utilities.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 11),
('b2000000-0000-0000-0003-000000000012', 'b0000000-0000-0000-0000-000000000001', 'A.7.12', 'Cabling Security', 'Cables carrying power, data, or supporting information services shall be protected from interception, interference, or damage.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 12),
('b2000000-0000-0000-0003-000000000013', 'b0000000-0000-0000-0000-000000000001', 'A.7.13', 'Equipment Maintenance', 'Equipment shall be maintained correctly to ensure availability, integrity, and confidentiality of information.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 13),
('b2000000-0000-0000-0003-000000000014', 'b0000000-0000-0000-0000-000000000001', 'A.7.14', 'Secure Disposal or Re-Use of Equipment', 'Items of equipment containing storage media shall be verified to ensure that any sensitive data and licensed software has been removed or securely overwritten prior to disposal or re-use.', 'Physical Controls', 'b2000000-0000-0000-0000-000000000003', 14);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b2000000-0000-0000-0000-000000000004', 'b0000000-0000-0000-0000-000000000001', 'A.8', 'Technological Controls', 'Controls related to technical security measures and systems.', 'Technological Controls', NULL, 13);
INSERT INTO framework_requirements (id, framework_id, code, name, description, category, parent_id, sort_order) VALUES
('b2000000-0000-0000-0004-000000000001', 'b0000000-0000-0000-0000-000000000001', 'A.8.1', 'User Endpoint Devices', 'Information stored on, processed by, or accessible via user endpoint devices shall be protected.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 1),
('b2000000-0000-0000-0004-000000000002', 'b0000000-0000-0000-0000-000000000001', 'A.8.2', 'Privileged Access Rights', 'The allocation and use of privileged access rights shall be restricted and managed.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 2),
('b2000000-0000-0000-0004-000000000003', 'b0000000-0000-0000-0000-000000000001', 'A.8.3', 'Information Access Restriction', 'Access to information and other associated assets shall be restricted in accordance with the established topic-specific policy on access control.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 3),
('b2000000-0000-0000-0004-000000000004', 'b0000000-0000-0000-0000-000000000001', 'A.8.4', 'Access to Source Code', 'Read and write access to source code, development tools, and software libraries shall be appropriately managed.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 4),
('b2000000-0000-0000-0004-000000000005', 'b0000000-0000-0000-0000-000000000001', 'A.8.5', 'Secure Authentication', 'Secure authentication technologies and procedures shall be implemented based on information access restrictions and the topic-specific policy on access control.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 5),
('b2000000-0000-0000-0004-000000000006', 'b0000000-0000-0000-0000-000000000001', 'A.8.6', 'Capacity Management', 'The use of resources shall be monitored and adjusted in line with current and expected capacity requirements.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 6),
('b2000000-0000-0000-0004-000000000007', 'b0000000-0000-0000-0000-000000000001', 'A.8.7', 'Protection Against Malware', 'Protection against malware shall be implemented and supported by appropriate user awareness.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 7),
('b2000000-0000-0000-0004-000000000008', 'b0000000-0000-0000-0000-000000000001', 'A.8.8', 'Management of Technical Vulnerabilities', 'Information about technical vulnerabilities of information systems in use shall be obtained, the organization''s exposure to such vulnerabilities shall be evaluated, and appropriate measures shall be taken.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 8),
('b2000000-0000-0000-0004-000000000009', 'b0000000-0000-0000-0000-000000000001', 'A.8.9', 'Configuration Management', 'Configurations, including security configurations, of hardware, software, services, and networks shall be established, documented, implemented, monitored, and reviewed.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 9),
('b2000000-0000-0000-0004-000000000010', 'b0000000-0000-0000-0000-000000000001', 'A.8.10', 'Information Deletion', 'Information stored in information systems, devices, or in any other storage media shall be deleted when no longer required.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 10),
('b2000000-0000-0000-0004-000000000011', 'b0000000-0000-0000-0000-000000000001', 'A.8.11', 'Data Masking', 'Data masking shall be used in accordance with the organization''s topic-specific policy on access control and other related topic-specific policies, and business requirements, taking applicable legislation into consideration.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 11),
('b2000000-0000-0000-0004-000000000012', 'b0000000-0000-0000-0000-000000000001', 'A.8.12', 'Data Leakage Prevention', 'Data leakage prevention measures shall be applied to systems, networks, and any other devices that process, store, or transmit sensitive information.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 12),
('b2000000-0000-0000-0004-000000000013', 'b0000000-0000-0000-0000-000000000001', 'A.8.13', 'Information Backup', 'Backup copies of information, software, and systems shall be maintained and regularly tested in accordance with the agreed topic-specific policy on backup.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 13),
('b2000000-0000-0000-0004-000000000014', 'b0000000-0000-0000-0000-000000000001', 'A.8.14', 'Redundancy of Information Processing Facilities', 'Information processing facilities shall be implemented with redundancy sufficient to meet availability requirements.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 14),
('b2000000-0000-0000-0004-000000000015', 'b0000000-0000-0000-0000-000000000001', 'A.8.15', 'Logging', 'Logs that record activities, exceptions, faults, and other relevant events shall be produced, stored, protected, and analysed.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 15),
('b2000000-0000-0000-0004-000000000016', 'b0000000-0000-0000-0000-000000000001', 'A.8.16', 'Monitoring Activities', 'Networks, systems, and applications shall be monitored for anomalous behaviour and appropriate actions taken to evaluate potential information security incidents.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 16),
('b2000000-0000-0000-0004-000000000017', 'b0000000-0000-0000-0000-000000000001', 'A.8.17', 'Clock Synchronization', 'The clocks of information processing systems used by the organization shall be synchronized to approved time sources.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 17),
('b2000000-0000-0000-0004-000000000018', 'b0000000-0000-0000-0000-000000000001', 'A.8.18', 'Use of Privileged Utility Programs', 'The use of utility programs that might be capable of overriding system and application controls shall be restricted and tightly controlled.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 18),
('b2000000-0000-0000-0004-000000000019', 'b0000000-0000-0000-0000-000000000001', 'A.8.19', 'Installation of Software on Operational Systems', 'Procedures and measures shall be implemented to securely manage software installation on operational systems.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 19),
('b2000000-0000-0000-0004-000000000020', 'b0000000-0000-0000-0000-000000000001', 'A.8.20', 'Networks Security', 'Networks and network devices shall be secured, managed, and controlled to protect information in systems and applications.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 20),
('b2000000-0000-0000-0004-000000000021', 'b0000000-0000-0000-0000-000000000001', 'A.8.21', 'Security of Network Services', 'Security mechanisms, service levels, and service requirements of network services shall be identified, implemented, and monitored.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 21),
('b2000000-0000-0000-0004-000000000022', 'b0000000-0000-0000-0000-000000000001', 'A.8.22', 'Segregation of Networks', 'Groups of information services, users, and information systems shall be segregated in the organization''s networks.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 22),
('b2000000-0000-0000-0004-000000000023', 'b0000000-0000-0000-0000-000000000001', 'A.8.23', 'Web Filtering', 'Access to external websites shall be managed to reduce exposure to malicious content.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 23),
('b2000000-0000-0000-0004-000000000024', 'b0000000-0000-0000-0000-000000000001', 'A.8.24', 'Use of Cryptography', 'Rules for the effective use of cryptography, including cryptographic key management, shall be defined and implemented.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 24),
('b2000000-0000-0000-0004-000000000025', 'b0000000-0000-0000-0000-000000000001', 'A.8.25', 'Secure Development Life Cycle', 'Rules for the secure development of software and systems shall be established and applied.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 25),
('b2000000-0000-0000-0004-000000000026', 'b0000000-0000-0000-0000-000000000001', 'A.8.26', 'Application Security Requirements', 'Information security requirements shall be identified, specified, and approved when developing or acquiring applications.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 26),
('b2000000-0000-0000-0004-000000000027', 'b0000000-0000-0000-0000-000000000001', 'A.8.27', 'Secure System Architecture and Engineering Principles', 'Principles for engineering secure systems shall be established, documented, maintained, and applied to any information system development activities.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 27),
('b2000000-0000-0000-0004-000000000028', 'b0000000-0000-0000-0000-000000000001', 'A.8.28', 'Secure Coding', 'Secure coding principles shall be applied to software development.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 28),
('b2000000-0000-0000-0004-000000000029', 'b0000000-0000-0000-0000-000000000001', 'A.8.29', 'Security Testing in Development and Acceptance', 'Security testing processes shall be defined and implemented in the development life cycle.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 29),
('b2000000-0000-0000-0004-000000000030', 'b0000000-0000-0000-0000-000000000001', 'A.8.30', 'Outsourced Development', 'The organization shall direct, monitor, and review the activities related to outsourced system development.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 30),
('b2000000-0000-0000-0004-000000000031', 'b0000000-0000-0000-0000-000000000001', 'A.8.31', 'Separation of Development, Test and Production Environments', 'Development, testing, and production environments shall be separated and secured.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 31),
('b2000000-0000-0000-0004-000000000032', 'b0000000-0000-0000-0000-000000000001', 'A.8.32', 'Change Management', 'Changes to information processing facilities and information systems shall be subject to change management procedures.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 32),
('b2000000-0000-0000-0004-000000000033', 'b0000000-0000-0000-0000-000000000001', 'A.8.33', 'Test Information', 'Test information shall be appropriately selected, protected, and managed.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 33),
('b2000000-0000-0000-0004-000000000034', 'b0000000-0000-0000-0000-000000000001', 'A.8.34', 'Protection of Information Systems During Audit Testing', 'Audit tests and other assurance activities involving assessment of operational systems shall be planned and agreed between the tester and appropriate management.', 'Technological Controls', 'b2000000-0000-0000-0000-000000000004', 34);
